import React, { Component } from "react";
import "./ToDoList.css";

let styles = { textDecoration: "line-through" };

class ToDoList extends Component {
  state = {
    text: "hookah",
    items: []
  };

  setup = (e) => {
    this.setState({ text: e.target.value });
  };

  getup = (e) => {
    let items = this.state.items;
    console.log(items);
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: Date.now(),
      completed: false
    };
    //    console.log(newItem);

    this.setState((state) => ({
      items: this.state.items.concat(newItem),
      text: ""
    }));
  };
  removeItem = (id) => {
    const remainder = this.state.items.filter((todo) => {
      if (todo.id !== id) return todo;
      console.log("data", this.state.items);
    });
    console.log("uuuu", remainder);
    this.setState({ items: remainder });
    console.log("set", remainder);
  };
  handleChange = (id) => {
    const updated = this.state.items.map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });

    this.setState({ items: updated });
    console.log("cccccc2", updated);
  };
  editItem=(item)=>{
    const inputF = document.getElementById('input');
    inputF.value=item;
    onChange=(e)=>{
        const updatedTask= e.target.value;
         this.state.items.find((item)=>{
          return item  === updatedTask
        })
      }

    const newArr=  inputF.onChange()
    this.setState({items : newArr})
    // const updateItem =this.state.items.filter(
    //   (items)=>{
    //     if(items.text!==item){
    //       return items
    //     }
    //   }
    // )
    // this.setState({items: updateItem})
  }

  render() {
    return (
      <div className="Table">
        <h1 className ="Header"> ToDo List</h1>
        <div className="InputDiv">
        <input id="input" placeholder="Type here ...."
        className="InputField" 
        onChange={this.setup} 
        value={this.state.text} />
        <button onClick={this.getup} className="Add">
          Add
        </button>
        </div>
        <div>
          <fieldset className="ListField">
            <legend>Your List For Today</legend>
        <List
          items={this.state.items}
          removeItem={this.removeItem}
          handleChange={this.handleChange}
          editItem={this.editItem}
        />
          </fieldset>
        </div>
      </div>
    );
  }
}

class List extends Component {
  render() {
    return (
      //console.log("props",this.props),
      <div className="List">
        <ul className="ListStyle">
          {this.props.items.map((item) => (
            <li className="ListItem" key={item.id} style={item.completed ? styles : null}>
              {" "}
              {item.text}
              <div className="ButtonsDiv">
              <button className="Removebtn" onClick={() => this.props.removeItem(item.id)}>
                remove
              </button>
              <button onClick={()=>this.props.editItem(item.text)}>Edit</button>
              <input
                className="checkmark"
                type="checkbox"
                checked={item.completed}
                onChange={() => this.props.handleChange(item.id)}
              />
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default ToDoList;
