import React, { Component } from 'react';




class Add extends Component {
    state = {
         text: 'hookah',
         items: []
       };

   setup = (e) => {
     this.setState({text: e.target.value});
   }

   getup = (e) => {
     let items = this.state.items;
     console.log(items);
     e.preventDefault();
     if(!this.state.text.length){
       return;
     }
     const newItem = {
       text: this.state.text,
       id: Date.now()
     }    
    //    console.log(newItem);


     this.setState(state => ({
       items: this.state.items.concat(newItem),
       text: ''
     }));
   }

    // removeItem = (props)=> {
    //     this.state.items.splice(props, 1)
    //     this.setState({items: this.state.items})
    //     console.log('remove',this.props)
    //     console.log('rreeemove',this.state.items)

    // };





     render(){
   return(
     <div className="Table">
       <h1> ToDo List</h1>
       <List items={this.state.items}
          // removeItem={this.removeItem}
       />
       <input onChange={this.setup}
       value={this.state.text} />
       <button onClick={this.getup}
       className="Add">Add</button>
       </div>
   )


     }}







   class List extends Component {

    removeItem = (props)=> {
        this.state.items.splice(props, 1)
        this.setState({items: this.state.items})
        console.log('remove',this.props)
        console.log('rreeemove',this.state.items)

    };



     render() {
       return (
           //console.log("props",this.props),
   <div className="List">
     <ul>
       {this.props.items.map((item)=>
       (
           <div> 
         <li key={item.id}> {item.text}
         <button onClick={()=>this.removeItem(item.id)} //onClick={this.props.removeItem(item.id)} //onClick(remove(item.id))
       >remove</button>
         </li>

</div>
       ))}
       </ul>
   </div> 
       );

       }
     }


export default Add;
