import React, { Component } from "react";
import useId from "react-id-generator";



const text = '';
class AddTask extends React.Component{

    state={
        tasks : []
    }
    onChangehanddler=(e)=>{
        this.setState({function (text){
            console.log(e.target.value)
            return text = e.target.value
        }})
    }
    addToTasks =(text)=>{
        this.state.tasks.push(text.trim());

    }

    AddToList=(state)=>{
        state.tasks.map(
            function(tasks){
            const idList = useId(tasks.length, "text");
               return <li key={idList}>
                {tasks}
                <br/>
                {new Date().toString()}
            </li>
            }
        )

    }



    render(){
        return <div> 
        <label>Enter your task</label>
        <input placeholder="Type here" onSubmit={this.onChangehanddler} />
        <button onSubmit={this.addToTasks}>ADD</button>
        <ul>
            <AddToList st/>
        </ul>
        </div>
    }

}
export default AddTask;
