import React , {Component} from 'react';
import './App.css';
import './AddComp'
import AddTask from './AddComp';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state={
     tasks : []
    };
};

addToTask(e,tasks){
  e.preventDefault();
  if(e.target.value.trim()!==''){
    let item =e.target.value.trim();
    this.setState({tasks : tasks.push(item)})
  }
  else { alert('enter task!')}
  e.target.value='';
};
render(){
  return (
    <div className="App">
      <header className="App-header">
       TO DO APP
      </header>
      <div> 
        <form onSubmit={this.addToTask}>
        <label>Enter your task</label>
        <input type="text" 
        placeholder="Type here..." 
        value={this.state.item}
        onChange={e=>this.value(e.target.value)} />
        <button type="submit">ADD</button>

        </form>
        </div>
        <div>
          <fieldset>
          <legend> Today List </legend>
            <ul>
            <AddTask />
            </ul>
          </fieldset>
        </div>
    </div>
  );}
}

export default App;
