import React, { Component } from "react";

import AddToList from './Appcomp';



const text = '';
class AddTask extends React.Component{

    state={
        tasks : []
    };
    onChangehanddler=(e)=>{
        this.setState({function (text){
            console.log(e.target.value)
            return text = e.target.value
        }})
    };
    addToTasks =(text)=>{
        this.state.tasks.push(text.trim());

    };


    render(){
        return <div> 
        <label>Enter your task</label>
        <input placeholder="Type here" onSubmit={this.onChangehanddler} />
        <button onSubmit={this.addToTasks}>ADD</button>
        <ul>
            <AddToList />
        </ul>
        </div>
    };

};
export default AddTask;
